(466, 'font effect', 'Design element applied to selected text.', '', 'n', '', 1, 'core'),
(467, 'font size', 'The height of an uppercase letter in a character set.', '', 'n', '', 1, 'core'),
(468, 'font style', 'Attributes of selected text, such as bold, italics, and underlining.', '', 'n', '', 1, 'core'),
(469, 'footer', 'Information that is placed at the bottom of one or more pages in a document.', '', 'n', '', 1, 'core'),
(470, 'footnote', 'A reference or comment, indicated by a number or symbol, that is shown at the bottom of a page.', '', 'n', '', 1, 'core'),
