(547, 'import', 'Bring in data produced by another application or on another system.', '', 'tv', '', 1, 'core'),
(548, 'important', 'Having great significance or value.', '', 'adj', '', 1, 'core'),
(549, 'in progress', 'Occurring at the moment.', 'Example: Do not turn off your computer while the update is in progress.', 'prep', '', 1, 'core'),
(550, 'inactive', 'Not currently enabled.', '', 'adj', '', 1, 'core'),
(551, 'inbox', 'The default folder where received messages are stored and retrieved.', '', 'n', '', 1, 'core'),
(552, 'include', 'Have as a part; make a part of.', 'Example 1: OpenOffice includes a word processor, spreadsheet, relational database, and drawing program.\r\n\r\nExample 2: Make sure to include your name and email address in your message.', 'tv', '', 1, 'core'),
(553, 'incoming', 'Arriving.', '', 'adj', '', 1, 'core'),
(554, 'incompatible', 'Not capable of being used with or connected to other devices or components without modification.', 'Example: Ahmed''s power charger was incompatible with Fatuma''s phone, so Fatuma could not use it to recharge her battery.', 'adj', '', 1, 'core'),
(555, 'increase', 'Make more or larger.', '', 'tv', '', 1, 'core'),
(556, 'indent', 'Begin a line or lines at a specified distance from the margin.', '', 'tv', '', 1, 'core'),
(557, 'indentation', 'The beginning of a line or lines at a specified distance from the margin.', '', 'n', '', 1, 'core'),
(558, 'index', 'A listing of items and their locations.', '', 'n', '', 1, 'core'),
(559, 'indicator', 'Something that shows the status of a variable entity.', 'Example: If you cannot hear sound when the the volume indicator is at its maximum level, make sure that your speakers are connected and the power is turned on.', 'n', '', 1, 'core'),
(560, 'infected', 'Containing a program or virus that can harm the system.', '', 'adj', '', 1, 'core'),
(561, 'information', 'Knowledge about a topic.', '', 'n', '', 1, 'core'),
(562, 'IT / information technology', 'Systems of hardware and software used to store, process, access and transmit information and data for any purpose.', 'IT is the industry or discipline involving the collection, dissemination, and management of data, typically through the use of computers.', 'n', '', 1, 'core'),
(563, 'initial', 'Placed at the beginning.', '', 'adj', '', 1, 'core'),
(564, 'initialize', 'Format a storage medium or prepare hardware for use.', '', 'tv', '', 1, 'core'),
(565, 'initials', 'The first letters of each part of a full name.', 'Example: President John Fitzgerald Kennedy is often referred to by his initials, JFK.', 'n', '', 1, 'core'),
(566, 'in-line', 'Occurring directly within a document.', 'Example: He wrote his comments to the email in-line, answering each question as he scrolled down.', 'adj', '', 1, 'core'),
(567, 'input', 'Data entered into a spreadsheet, database, or other program.', '', 'n', '', 1, 'core'),
(568, 'input', 'Enter data into a spreadsheet, database, or other program.', '', 'tv', '', 1, 'core'),
