(994, 'session', 'A time interval during which a user''s connection to a website is actively maintained.', '', 'n', '', 1, 'core'),
(995, 'set', 'Establish configuration options.', '', 'tv', '', 1, 'core'),
(996, 'set up', 'Configure for use.', '', 'tv', '', 1, 'core'),
