(1015, 'single-sided', 'Printed on only one face of a piece of paper.', '', 'adj', '', 1, 'core'),
(1016, 'site', 'A collection of interlinked documents made available on the Internet through a common homepage.', '', 'n', '', 1, 'core'),
(1428, 'undelete', 'Restore something that has been removed or erased.', '', 'tv', '', 1, 'core'),
