(2394, 'user profile', 'Information about a person who interacts directly with a website, hardware device, or program.', '', 'n', '', 1, 'core'),
(2395, 'using', 'Employing or putting into service.', '', 'part', '', 1, 'core'),
(2396, 'valid until', 'Can be used prior to a specified date or time.', 'Example: Your membership is valid until 15 January, 2010.', 'adj', '', 1, 'core'),
(2397, 'vector graphics', 'Art composed of lines and curves defined by mathematical formulas.', 'Vector graphics is the use of geometrical primitives such as points, lines, curves, and shapes or polygon(s), which are all based upon mathematical equations, to represent images in computer graphics.\r\n\r\nUsed for line drawings, logos, and other images that require smooth edges. Made up of mathematically defined curves and line segments called vectors. It is beneficial in printing, due to ability to be enlarged without loss of detail.', 'adj', '', 1, 'core'),
(2398, 'vendor', 'Person or company that sells something.', 'Example: Please contact your software vendor for product upgrades and add-ons.', 'n', '', 1, 'core'),
(2399, 'verbose', 'A mode or setting that displays or gets extended information.', '', 'adj', '', 1, 'core'),
(2400, 'verification', 'The process of confirmation that something is authentic or correct.', '', 'adj', '', 1, 'core'),
(2401, 'verified', 'Be confirmed as authentic.', '', 'adj', '', 1, 'core'),
(2402, 'verifying', 'Confirming or establishing authenticity.', '', 'adj', '', 1, 'core'),
(2403, 'vertically', 'Aligned in an "up-down" direction, perpendicular to the horizon or horizontal plane.', '', 'adv', '', 1, 'core'),
